class n extends HTMLElement{connectedCallback(){this.attachShadow({mode:"open"}),this.shadowRoot.innerHTML="\n    <style>\n      \n      slot {\n        display: grid;\n        grid-template-columns: 1fr;\n        grid-auto-rows: minmax(44px, auto);\n        grid-gap: var(--grid-gap);\n        margin: 0 var(--grid-gap) var(--grid-gap) var(--grid-gap);\n        \n        max-width: 765px;\n        margin: 0 auto;\n        margin-top: 2rem;\n      }\n\n      ::slotted(.header) {\n        background-color: var(--color-blue);\n        color: var(--color-white);\n        font-size: var(--font-size-small);\n        font-weight: var(--font-weight-boldest);\n\n        max-width: 100vw;\n        text-transform: uppercase;\n\n        grid-column: 1 / -1;\n        padding: 0 var(--grid-panel-padding);\n        margin: 0 var(--grid-gap-negative);\n      }\n\n      ::slotted(.header.red) {\n        background-color: var(--color-red);\n      }\n\n      ::slotted(h2) {\n        font-size: var(--font-size-medium);\n        margin: 0;\n      }\n    \n    </style>\n    \n    <slot></slot>\n    "}}customElements.define("page-one-column",n);
